import React, { useState, useEffect } from "react";
import axios from "axios";
import {Link} from 'react-router-dom'

const Read = () => {
  const [data, SetData] = useState([]);

  const[currentpage,setCurrentPage]=useState(1)
  const recordperpage = 5;
  const lastIndex= currentpage*recordperpage
  const firstIndex = lastIndex-recordperpage
  const records = getdata.slice(firstIndex,lastIndex)
  const npage = Math.ceil(data.length/recordperpage)
  const number = [...Array(npage+1).keys()].slice(1)
  function getdata() {
    axios
      .get("https://64be179a2320b36433c80a7d.mockapi.io/project")
      // promise
      .then((res) => {
        console.log(res.data);
        SetData(res.data);
      });
  }
  const handleDelete = (id) => {
    axios.delete(`https://64be179a2320b36433c80a7d.mockapi.io/project/${id}`
    ).then(() => {
      getdata();
    });
  }
  useEffect(() => {
    getdata();
  }, []);

  const setLocalData = (id,name,email) =>{
    localStorage.setItem("id",id);
    localStorage.setItem("name",name);
    localStorage.setItem("email",email);

  }
  function perpage(){
    if(setCurrentPage !== firstIndex){
      setCurrentPage(currentpage - 1)
    }
  }
  function changepage(id){
    setCurrentPage(id)
  }
  function nextpage(){
    if(currentpage !== lastIndex){
      setCurrentPage(currentpage + 1)
    }


  }
  


  return (
    <>
      <div className="d-flex justify-content-between m-3">
      <h2>Read </h2>
      <Link to="/">
      <button className="btn btn-secondary">Create</button>
      </Link>
      </div>
      
      <table className="table">
        <thead>
          <tr>
            <th scope="col">id</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Edit</th>
            <th scope="col">Delete</th>
          </tr>
        </thead>
        {data.map((eachData) => {
          return (
            <>
              <tbody>
                <tr>
                  <th scope="row">{eachData.id}</th>
                  <td>{eachData.name}</td>
                  <td>{eachData.email}</td>
                  <td>
                 <Link to="/update">
                 <button className="btn btn-success" onClick={()=>setLocalData(
                  eachData.id,
                  eachData.name,
                  eachData.email

                 )
                 }>
                 Edit</button>
                 </Link>
                    
                  </td>
                  <td>
                    <button className="btn btn-danger" onClick={()=>handleDelete(eachData.id)}>Delete</button>
                  </td>
                </tr>
              </tbody>
            </>
          );
        })}
      </table>
      <nav>
        <ul className="pagination">
          <li className="page-item">
            <a href="#" className="page-link" onClick={perpage}>Previous</a>
          </li>
          {
            number.map((n,i)=>{
              <li className={`page-item ${currentpage === n ?  'active':''}`} key={i}>
                <a href="#" className="page-item" onClick={changepage}>{n}</a>
              </li>
            })
          }
          <li className="page-item">
            <a href="#" className="page-link" onClick={nextpage}>Next</a>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Read;
